query.sfwSql:
IF OBJECT_ID('TEMPDB.DBO.#TEMP_WAIT_TABLE') IS NULL 
BEGIN
	CREATE TABLE #TEMP_WAIT_TABLE(
		WAIT_TYPE VARCHAR(50) NOT NULL,
		WAIT_TYPE_DESCRIPTION VARCHAR(MAX) NOT NULL
	);

	INSERT INTO #TEMP_WAIT_TABLE VALUES('SOS_SCHEDULER_YIELD','OCCURS WHEN A TASK VOLUNTARILY YIELDS THE SCHEDULER FOR OTHER TASKS TO EXECUTE. DURING THIS WAIT THE TASK IS WAITING FOR ITS QUANTUM TO BE RENEWED.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('CXPACKET','OCCURS WITH PARALLEL QUERY PLANS WHEN WAITING TO SYNCHRONIZE THE QUERY PROCESSOR EXCHANGE ITERATOR, AND WHEN PRODUCING AND CONSUMING ROWS. IF WAITING IS EXCESSIVE AND CANNOT BE REDUCED BY TUNING THE QUERY (SUCH AS ADDING INDEXES), CONSIDER ADJUSTING THE COST THRESHOLD FOR PARALLELISM OR LOWERING THE MAX DEGREE OF PARALLELISM (MAXDOP).');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('WRITELOG','OCCURS WHILE WAITING FOR A LOG FLUSH TO COMPLETE. COMMON OPERATIONS THAT CAUSE LOG FLUSHES ARE CHECKPOINTS AND TRANSACTION COMMITS.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('ASYNC_NETWORK_IO','OCCURS ON NETWORK WRITES WHEN THE TASK IS BLOCKED BEHIND THE NETWORK. VERIFY THAT THE CLIENT IS PROCESSING DATA FROM THE SERVER.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_S','OCCURS WHEN A TASK IS WAITING TO ACQUIRE A SHARED LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_U','OCCURS WHEN A TASK IS WAITING TO ACQUIRE AN UPDATE LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_X','OCCURS WHEN A TASK IS WAITING TO ACQUIRE AN EXCLUSIVE LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('PAGEIOLATCH_SH','OCCURS WHEN A TASK IS WAITING ON A LATCH FOR A BUFFER THAT IS IN AN I/O REQUEST. THE LATCH REQUEST IS IN SHARED MODE. LONG WAITS MAY INDICATE PROBLEMS WITH THE DISK SUBSYSTEM.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_IX','OCCURS WHEN A TASK IS WAITING TO ACQUIRE AN INTENT EXCLUSIVE (IX) LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_IU','OCCURS WHEN A TASK IS WAITING TO ACQUIRE AN INTENT UPDATE (IU) LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('PAGEIOLATCH_EX','OCCURS WHEN A TASK IS WAITING ON A LATCH FOR A BUFFER THAT IS IN AN I/O REQUEST. THE LATCH REQUEST IS IN EXCLUSIVE MODE. LONG WAITS MAY INDICATE PROBLEMS WITH THE DISK SUBSYSTEM.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('PAGEIOLATCH_UP','OCCURS WHEN A TASK IS WAITING ON A LATCH FOR A BUFFER THAT IS IN AN I/O REQUEST. THE LATCH REQUEST IS IN UPDATE MODE. LONG WAITS MAY INDICATE PROBLEMS WITH THE DISK SUBSYSTEM.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_SCH_M','OCCURS WHEN A TASK IS WAITING TO ACQUIRE A SCHEMA MODIFY LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('RESOURCE_SEMAPHORE','OCCURS WHEN A QUERY MEMORY REQUEST CANNOT BE GRANTED IMMEDIATELY DUE TO OTHER CONCURRENT QUERIES. HIGH WAITS AND WAIT TIMES MAY INDICATE EXCESSIVE NUMBER OF CONCURRENT QUERIES, OR EXCESSIVE MEMORY REQUEST AMOUNTS.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_SCH_S','OCCURS WHEN A TASK IS WAITING TO ACQUIRE A SCHEMA SHARE LOCK.');
	INSERT INTO #TEMP_WAIT_TABLE VALUES('LCK_M_IS','OCCURS WHEN A TASK IS WAITING TO ACQUIRE AN INTENT SHARED (IS) LOCK.');
END;

SELECT WAIT_STATS.WAIT_TYPE,
WAITING_TASKS_COUNT,
WAIT_TIME_MS,
MAX_WAIT_TIME_MS,
ROUND(CAST(WAIT_TIME_MS AS FLOAT)/ WAITING_TASKS_COUNT,2) AVG_WAIT_TIME_MS,
TEMP.WAIT_TYPE_DESCRIPTION DESCRIPTION
FROM SYS.DM_OS_WAIT_STATS WAIT_STATS
INNER JOIN #TEMP_WAIT_TABLE TEMP ON TEMP.WAIT_TYPE = WAIT_STATS.WAIT_TYPE
WHERE (WAIT_STATS.WAIT_TYPE IN ('CXPACKET', 'RESOURCE_SEMAPHORE', 'SOS_SCHEDULER_YIELD', 'WRITELOG', 'ASYNC_NETWORK_IO')
OR
WAIT_STATS.WAIT_TYPE IN ('LCK_M_SCH_S', 'LCK_M_SCH_M', 'LCK_M_S', 'LCK_M_U', 'LCK_M_X', 'LCK_M_IS', 'LCK_M_IU', 'LCK_M_IX')
OR
WAIT_STATS.WAIT_TYPE LIKE 'PAGEIOLATCH%')
AND 
WAIT_TIME_MS > 0
